        public static int pairs(int k, List<int> arr)
        {
           // Console.WriteLine(string.Join(",", arr));

            int result = 0;
            arr.Sort((i, i1) => i - i1);

            while (arr.Count != 0)
            {
                int first = arr.First();
                arr.RemoveAt(0);

                int binarySearch = arr.BinarySearch(first, new MyClass(k) );

                if (binarySearch > 0)
                {
                    result++;
                    //arr.RemoveAt(binarySearch);

                   // binarySearch = arr.BinarySearch(first, new MyClass(k));
                }
            }

            return result;
        }

        public class MyClass: IComparer<int>
        {
            private readonly int value;

            public MyClass(int value)
            {
                this.value = value;
            }

            public int Compare(int x, int y)
            {
                return Math.Abs(x - y) - value;
            }
        }
